INSERT INTO public.activesub_info (name, active, description) VALUES ('Кофеин', true, 'Стимулятор центральной нервной системы, повышает бодрость, концентрацию и снижает усталость. Содержится в кофе, чае, энергетических напитках и некоторых лекарствах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Ибупрофен', true, 'Нестероидное противовоспалительное средство (НПВС), применяется для уменьшения боли, воспаления и температуры.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Лактоза', false, 'Углевод, содержащийся в молочных продуктах. Часто используется как наполнитель в лекарственных препаратах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Аскорбиновая кислота (витамин C)', true, 'Антиоксидант, необходим для нормального функционирования иммунной системы, кожи и соединительной ткани.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Этанол', true, 'Спирт, используемый в медицине как антисептик, растворитель или консервант. Также обладает седативным эффектом.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Глицерин', false, 'Используется как увлажнитель, растворитель или стабилизатор в лекарственных и косметических средствах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Димедрол', true, 'Антигистаминное средство, применяется для лечения аллергических реакций, а также как седативное и противорвотное средство.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Кальция карбонат', true, 'Антацид, используется для нейтрализации кислоты в желудке, а также как источник кальция.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Мелатонин', true, 'Гормон, регулирующий циклы сна и бодрствования. Используется для борьбы с бессонницей и джетлагом.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Ацетилсалициловая кислота (Аспирин)', true, 'Нестероидное противовоспалительное средство, обладающее анальгезирующим, жаропонижающим и антиагрегантным эффектом.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Левотироксин', true, 'Препарат для заместительной терапии при гипотиреозе, содержит синтетический аналог гормонов щитовидной железы.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Сальбутамол', true, 'Бронхолитик, применяемый при астме и хронической обструктивной болезни лёгких (ХОБЛ) для облегчения дыхания.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Флуоксетин', true, 'Антидепрессант из группы селективных ингибиторов обратного захвата серотонина (СИОЗС), применяется при депрессии и тревожных расстройствах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Метформин', true, 'Препарат для лечения диабета 2 типа, помогает снизить уровень сахара в крови и повысить чувствительность к инсулину.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Лоперамид', true, 'Противодиарейное средство, замедляет перистальтику кишечника и уменьшает частоту стула.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Декстрометорфан', true, 'Противокашлевое средство, используемое при сухом кашле, действует на кашлевой центр в головном мозге.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Сорбитол', false, 'Сахарный спирт, используемый как подсластитель и наполнитель в лекарственных препаратах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Тальк', false, 'Вспомогательный компонент в таблетках, используется как смазывающее вещество.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Сахароза', false, 'Подсластитель, применяется в сиропах, таблетках и других лекарственных формах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Крахмал', false, 'Наполнитель в таблетках, используется для придания формы и стабильности лекарственным препаратам.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Парафин', false, 'Используется в мазях и свечах как основа и связующее вещество.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Желатин', false, 'Используется в капсулах и таблетках как загуститель и желирующее вещество.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Магния стеарат', false, 'Антифрикционный агент, применяется в производстве таблеток для предотвращения их прилипания к пресс-формам.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Целлюлоза микрокристаллическая', false, 'Наполнитель в таблетках, используется для придания структуры и стабильности.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Пропиленгликоль', false, 'Растворитель, увлажнитель и стабилизатор, используется в жидких лекарственных формах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Диоксид кремния', false, 'Антислеживающий агент, применяется в порошковых и таблетированных препаратах.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Глюкоза', false, 'Источник энергии, используется в растворах для внутривенного введения и в составе таблеток.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Пищевые красители', false, 'Используются в оболочках таблеток и капсул для придания цвета.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Ароматизаторы', false, 'Добавляются в сиропы, жевательные таблетки и другие лекарственные формы для улучшения вкуса.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Лецитин', false, 'Эмульгатор, применяется в мягких капсулах и жидких формах лекарств.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Вода', false, 'Растворитель, используемый в большинстве лекарственных форм для растворения или разбавления веществ.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Парабены', false, 'Консерванты, используемые в кремах, мазях и жидких лекарственных препаратах для предотвращения роста микробов.');
INSERT INTO public.activesub_info (name, active, description) VALUES ('Парацетамол', true, 'Стимулятор центральной нервной системы, повышает бодрость, концентрацию и снижает усталость. Содержится в чае, энергетических напитках и некоторых лекарствах.');
